mapper.select:
select

from admin

  name like #{name}
  type = #{type}


  order by id desc limit #{offset}, #{limit}

select count(*) from admin

  name like #{name}
  type = #{type}

mapper.insert:
insert into admin() values (
default, #{name}, #{password}, #{type}, now(), #{creatorId}, now(), #{updaterId}
)

mapper.update:
update admin set
   name=#{name}, password=#{password}, type=#{type}, update_time=now(), updater_id=#{updaterId}
where id = #{id}
