mapper.select:
select

from bill

  master_id = #{masterId}
  member_id = #{memberId}
  createTimeStart >= #{createTimeStart}
  createTimeEnd <= #{createTimeEnd}


  order by id desc limit #{offset}, #{limit}

select count(*) from bill

  master_id = #{masterId}
  member_id = #{memberId}
  createTimeStart >= #{createTimeStart}
  createTimeEnd <= #{createTimeEnd}

mapper.insert:
insert into bill() values (
default,

  0
  #{masterId}

,
#{memberId}, #{transType}, #{amount}, now(), #{creatorId}
);

  update bill set master_id=@@IDENTITY where id=@@IDENTITY;
