mapper.select:
select

from member

  name like #{name}
  mobile like #{mobile}
  id_card_no like #{idCardNo}


  order by id desc limit #{offset}, #{limit}

select count(*) from member

  name like #{name}
  mobile like #{mobile}
  id_card_no like #{idCardNo}

mapper.insert:
insert into member() values (
default, #{name}, #{idCardNo}, #{mobile}, #{password}, #{sex}, #{birthday}, #{level},
#{storedAmount}, #{consumeAmount},
#{integralAmount}, #{lastTransTime}, now(), #{creatorId}, now(), #{updaterId}
)

mapper.update:
update member set
   name=#{name}, id_card_no=#{idCardNo}, mobile=#{mobile}, password=#{password},
   sex=#{sex}, birthday=#{birthday}, level=#{level}, update_time=now(), updater_id=#{updaterId}
where id = #{id}
